import { useEffect, useState, useRef } from "react";
import { io } from "socket.io-client";
import OnlineStatus from "../components/OnlineStatus";
import axios from "axios";

export default function Chat({ user }) {
  const [socket, setSocket] = useState(null);
  const [text, setText] = useState("");
  const [receiverPhone, setReceiverPhone] = useState("");
  const [messages, setMessages] = useState([]);
  const messagesEndRef = useRef(null);
  const [receiverId, setReceiverId] = useState(null);

  // Scroll chat to bottom
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };
  useEffect(scrollToBottom, [messages]);

  // Fetch past messages
  const fetchMessages = async (id) => {
    if (!id || !user?.id) return;
    try {
      const res = await axios.get(`http://localhost:5000/api/messages/${id}?currentUserId=${user.id}`);
      setMessages(res.data);
    } catch (err) {
      console.error("Fetch messages error:", err.response?.data || err.message);
    }
  };

  // Socket.io connection
  useEffect(() => {
    if (!user?.id) return;

    const newSocket = io("http://localhost:5000", {
      transports: ["websocket", "polling"],
      auth: { token: localStorage.getItem("token") },
    });

    newSocket.on("connect", () => {
      console.log("✅ Connected to socket:", newSocket.id);
      setSocket(newSocket);
    });

    // Receive messages
    newSocket.on("receiveMessage", (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    newSocket.on("disconnect", () => setSocket(null));

    return () => newSocket.disconnect();
  }, [user]);

  const sendMessage = async () => {
    if (!socket) return;
    if (!text || !receiverPhone) return alert("Enter phone and message");

    try {
      // 1️⃣ Fetch receiver ID by phone
      const res = await axios.get(`http://localhost:5000/api/auth/findByPhone/${receiverPhone}`);
      const id = res.data._id;
      setReceiverId(id);

      // 2️⃣ Load previous messages if first time
      fetchMessages(id);

      // 3️⃣ Emit message
      socket.emit("sendMessage", { receiverPhone, text });

      // 4️⃣ Optimistically add message locally with delivered status
      setMessages((prev) => [
        ...prev,
        { senderId: user.id, receiverId: id, text, createdAt: new Date(), delivered: true },
      ]);
      setText("");
    } catch (err) {
      console.error("Send message error:", err.response?.data || err.message);
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: "auto", padding: 20, fontFamily: "sans-serif" }}>
      <OnlineStatus user={user} />
      <h2 style={{ textAlign: "center" }}>Chat App</h2>

      <div style={{ marginBottom: 10 }}>
        <label>Receiver Phone:</label>
        <input
          type="text"
          value={receiverPhone}
          onChange={(e) => setReceiverPhone(e.target.value)}
          placeholder="Enter phone number"
          style={{ width: "100%", padding: 8, borderRadius: 5, border: "1px solid #ccc", marginTop: 4 }}
        />
      </div>

      <div
        style={{
          border: "1px solid #ccc",
          borderRadius: 5,
          height: 300,
          overflowY: "auto",
          padding: 10,
          background: "#fafafa",
          marginBottom: 10,
        }}
      >
        {messages.length === 0 && <p style={{ textAlign: "center", color: "#999" }}>No messages yet</p>}
        {messages.map((m, i) => (
          <div key={i} style={{ textAlign: m.senderId === user.id ? "right" : "left", marginBottom: 6 }}>
            <span
              style={{
                display: "inline-block",
                background: m.senderId === user.id ? "#dcf8c6" : "#fff",
                padding: 8,
                borderRadius: 8,
                maxWidth: "80%",
                wordBreak: "break-word",
                position: "relative",
              }}
            >
              {m.text}
              {m.senderId === user.id && (
                <span
                  style={{
                    fontSize: 10,
                    marginLeft: 4,
                    color: m.delivered ? "green" : "red",
                    position: "absolute",
                    bottom: -14,
                    right: 0,
                  }}
                >
                  {m.delivered ? "✓" : "✗"}
                </span>
              )}
            </span>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div style={{ display: "flex", gap: 6 }}>
        <input
          type="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Type a message..."
          style={{ flexGrow: 1, padding: 8, borderRadius: 5, border: "1px solid #ccc" }}
        />
        <button
          onClick={sendMessage}
          disabled={!socket}
          style={{
            padding: "8px 12px",
            border: "none",
            borderRadius: 5,
            background: socket ? "#4CAF50" : "#999",
            color: "white",
            cursor: socket ? "pointer" : "not-allowed",
          }}
        >
          {socket ? "Send" : "Connecting..."}
        </button>
      </div>
    </div>
  );
}
