import { useEffect, useState, useRef } from "react";
import { io } from "socket.io-client";
import OnlineStatus from "../components/OnlineStatus";
import axios from "axios";

export default function Chat({ user }) {
  const [socket, setSocket] = useState(null);
  const [text, setText] = useState("");
  const [receiverPhone, setReceiverPhone] = useState("");
  const [receiverId, setReceiverId] = useState(null); // store receiver _id
  const [messages, setMessages] = useState([]);
  const messagesEndRef = useRef(null);

  // Scroll to bottom whenever messages change
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };
  useEffect(scrollToBottom, [messages]);

  // Socket.io connection
  useEffect(() => {
    if (!user?.id) return;

    const newSocket = io("http://localhost:5000", {
      transports: ["websocket"],
      auth: { token: localStorage.getItem("token") },
    });

    newSocket.on("connect", () => {
      console.log("✅ Connected to socket:", newSocket.id);
      setSocket(newSocket);
    });

    newSocket.on("receiveMessage", (msg) => {
      // Only add message if it belongs to this chat
      if (
        (msg.senderId === user.id && msg.receiverId === receiverId) ||
        (msg.senderId === receiverId && msg.receiverId === user.id)
      ) {
        setMessages((prev) => [...prev, msg]);
      }
    });

    newSocket.on("disconnect", () => setSocket(null));

    return () => newSocket.disconnect();
  }, [user, receiverId]);

  // Fetch messages from backend when receiver changes
  const fetchMessages = async (receiverPhone) => {
    try {
      const receiver = await axios.get(`http://localhost:5000/api/auth/user/${receiverPhone}`);
      if (!receiver.data?._id) return alert("User not found");

      setReceiverId(receiver.data._id);

      const { data } = await axios.get(
        `http://localhost:5000/api/messages/${receiver.data._id}?senderId=${user.id}`
      );
      setMessages(data);
    } catch (err) {
      console.error("Fetch messages error:", err.response?.data || err.message);
    }
  };

  // Send message to backend
  const sendMessage = () => {
    if (!socket) return;
    if (!text || !receiverPhone) return alert("Enter phone and message");
    if (!receiverId) return fetchMessages(receiverPhone); // Ensure receiverId is set

    socket.emit("sendMessage", { receiverPhone, text });
    setText("");
  };

  // When receiver phone changes, fetch chat history
  useEffect(() => {
    if (receiverPhone) fetchMessages(receiverPhone);
  }, [receiverPhone]);

  return (
    <div style={{ maxWidth: 400, margin: "auto", padding: 20, fontFamily: "sans-serif" }}>
      <OnlineStatus user={user} />

      <h2 style={{ textAlign: "center" }}>Chat App</h2>

      <div style={{ marginBottom: 10 }}>
        <label>Receiver Phone:</label>
        <input
          type="text"
          value={receiverPhone}
          onChange={(e) => setReceiverPhone(e.target.value)}
          placeholder="Enter phone number"
          style={{ width: "100%", padding: 8, borderRadius: 5, border: "1px solid #ccc", marginTop: 4 }}
        />
      </div>

      <div
        style={{
          border: "1px solid #ccc",
          borderRadius: 5,
          height: 300,
          overflowY: "auto",
          padding: 10,
          background: "#fafafa",
          marginBottom: 10,
        }}
      >
        {messages.length === 0 && (
          <p style={{ textAlign: "center", color: "#999" }}>No messages yet</p>
        )}

        {messages.map((m, i) => (
          <div key={i} style={{ textAlign: m.senderId === user.id ? "right" : "left", marginBottom: 6 }}>
            <span
              style={{
                display: "inline-block",
                background: m.senderId === user.id ? "#dcf8c6" : "#fff",
                padding: 8,
                borderRadius: 8,
                maxWidth: "80%",
                wordBreak: "break-word",
              }}
            >
              {m.text}
            </span>
          </div>
        ))}

        <div ref={messagesEndRef} />
      </div>

      <div style={{ display: "flex", gap: 6 }}>
        <input
          type="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Type a message..."
          style={{ flexGrow: 1, padding: 8, borderRadius: 5, border: "1px solid #ccc" }}
        />
        <button
          onClick={sendMessage}
          disabled={!socket}
          style={{
            padding: "8px 12px",
            border: "none",
            borderRadius: 5,
            background: socket ? "#4CAF50" : "#999",
            color: "white",
            cursor: socket ? "pointer" : "not-allowed",
          }}
        >
          {socket ? "Send" : "Connecting..."}
        </button>
      </div>
    </div>
  );
}
