import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import API from "../api";

const Chat = ({ user }) => {
  const [socket, setSocket] = useState(null);
  const [contactPhone, setContactPhone] = useState("");
  const [text, setText] = useState("");
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    if (!user) return;
    const newSocket = io("http://localhost:5000", { query: { userId: user._id } });
    setSocket(newSocket);

    newSocket.on("receiveMessage", (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    return () => newSocket.disconnect();
  }, [user]);

  const fetchChat = async () => {
    if (!contactPhone) return;
    // Get userId of contact from phone
    const contact = await API.get(`/auth/findByPhone/${contactPhone}`);
    const res = await API.get(`/messages/${user._id}/${contact.data._id}`);
    setMessages(res.data);
  };

  const sendMessage = () => {
  if (!socket) {
    console.warn("Socket not connected yet!");
    return;
  }
  if (!text || !contactPhone) return;

  socket.emit("sendMessage", {
    senderId: user._id,
    receiverPhone: contactPhone,
    text,
  });

  setMessages([...messages, { senderId: user._id, text, createdAt: new Date() }]);
  setText("");
};


  return (
    <div>
      <h2>Chat</h2>
      <input
        placeholder="Enter contact phone number"
        value={contactPhone}
        onChange={(e) => setContactPhone(e.target.value)}
      />
      <button onClick={fetchChat}>Load Chat</button>

      <div style={{ border: "1px solid gray", padding: 10, marginTop: 10, height: 300, overflowY: "scroll" }}>
        {messages.map((m, i) => (
          <div key={i} style={{ textAlign: m.senderId === user._id ? "right" : "left" }}>
            <p><strong>{m.senderId === user._id ? "You" : "Friend"}:</strong> {m.text}</p>
          </div>
        ))}
      </div>

      <input value={text} onChange={(e) => setText(e.target.value)} placeholder="Type a message" />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default Chat;
