import { useState, useEffect, useRef } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import "./AIChat.css"; // Import the external CSS
import { useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";


const personas = [
  { id: "friendly", name: "Friendly AI", description: "Polite & helpful" },
  { id: "sarcastic", name: "Sarcastic AI", description: "Funny & witty" },
  { id: "coder", name: "Coder AI", description: "Expert developer" },
  { id: "romantic", name: "Romantic AI", description: "Loving & sweet" },
];

export default function AIChat({ user, setUser }) {
  const [selectedPersona, setSelectedPersona] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  //logout
  const navigate = useNavigate();

  const logout = () => {
    localStorage.removeItem("token");  
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  // Scroll to bottom when new message arrives
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const sendMessage = async () => {
    if (!input.trim() || !selectedPersona) return;

    const userMsg = { sender: "user", text: input, timestamp: new Date() };
    setMessages((prev) => [...prev, userMsg]);
    setInput("");
    setLoading(true);

    try {
      const formattedMessages = messages.map((msg) => ({
        role: msg.sender === "user" ? "user" : "assistant",
        content: msg.text,
      }));
      formattedMessages.push({ role: "user", content: input });

      const res = await axios.post("http://localhost:5000/api/ai/chat", {
        messages: formattedMessages,
        persona: selectedPersona,
      });

      const aiReply = res.data.assistantMessage.content;
      setMessages((prev) => [
        ...prev,
        { sender: "ai", text: aiReply, timestamp: new Date() },
      ]);
    } catch (error) {
      setMessages((prev) => [
        ...prev,
        { sender: "ai", text: "Error contacting AI", timestamp: new Date() },
      ]);
    }

    setLoading(false);
  };

  const formatTime = (date) => {
    const d = new Date(date);
    return `${d.getHours()}:${d.getMinutes().toString().padStart(2, "0")}`;
  };

  return (
    <div>
      <Navbar/>
    <div className="ai-chat-container">
      
      {/* LEFT PANEL — PERSONA LIST */}
      <div className="ai-chat-left">
        
        <h2>AI Personas</h2>
        {personas.map((p) => (
          <motion.div
            whileHover={{ scale: 1.02 }}
            key={p.id}
            onClick={() => setSelectedPersona(p.id)}
            className={`ai-persona ${selectedPersona === p.id ? "selected" : ""}`}
          >
            <h3>{p.name}</h3>
            <p>{p.description}</p>
          </motion.div>
        ))}
      </div>

      {/* RIGHT PANEL — CHAT WINDOW */}
      <div className="ai-chat-right">
        <div className="ai-messages">
          {selectedPersona ? (
            messages.map((msg, index) => (
              <div key={index} className={`ai-message ${msg.sender}`}>
                {msg.text}
                <span className="timestamp">{formatTime(msg.timestamp)}</span>
              </div>
            ))
          ) : (
            <div className="ai-no-persona">Select a persona to start chatting</div>
          )}
          <div ref={messagesEndRef}></div>
        </div>

        {selectedPersona && (
          <div className="ai-input-bar">
            <input
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type a message..."
              onKeyDown={(e) => e.key === "Enter" && sendMessage()}
            />
            <button onClick={sendMessage} disabled={loading}>
              Send
            </button>
          </div>
        )}
      </div>
    </div>
    </div>
  );
}
