import { useEffect, useState, useRef } from "react";
import { io } from "socket.io-client";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function ChatApp({ user, setUser }) {
  const [socket, setSocket] = useState(null);
  const [chatList, setChatList] = useState([]);
  const [searchPhone, setSearchPhone] = useState("");
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const messagesEndRef = useRef(null);
  const [typingUser, setTypingUser] = useState(null);

  // FIXED: useRef for proper long press
  const pressTimer = useRef(null);

  const handlePressStart = (messageId, receiverId) => {
    pressTimer.current = setTimeout(() => {
      if (window.confirm("Delete this message?")) {
        deleteMessage(messageId, receiverId);
      }
    }, 600);
  };

  const handlePressEnd = () => {
    clearTimeout(pressTimer.current);
  };

  // Auto scroll
  const scrollToBottom = () =>
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  useEffect(scrollToBottom, [messages]);

  // SOCKET CONNECTION
  useEffect(() => {
    const s = io("http://localhost:5000", {
      transports: ["websocket", "polling"],
      auth: { token: localStorage.getItem("token") },
    });

    s.on("connect", () => setSocket(s));

    // RECEIVE MESSAGE
    s.on("receiveMessage", (msg) => {
      if (
        selectedUser &&
        (msg.senderId === selectedUser._id ||
          msg.receiverId === selectedUser._id)
      ) {
        setMessages((prev) => [...prev, msg]);
      }
    });

    // RECEIVE DELETE EVENT
    s.on("messageDeleted", (messageId) => {
      setMessages((prev) => prev.filter((m) => m._id !== messageId));
    });

    return () => s.disconnect();
  }, [selectedUser]);

  // Fetch chat list
  useEffect(() => {
    const fetchChats = async () => {
      try {
        const res = await axios.get(
          `http://localhost:5000/api/chats/${user.id}`
        );
        setChatList(res.data);
      } catch (err) {
        console.error("Fetch chat list error:", err);
      }
    };
    fetchChats();
  }, [user.id]);

  // Start chat
  const startChat = async (phone) => {
    const targetPhone = (phone || searchPhone).trim();
    if (!targetPhone) return alert("Enter phone number");

    try {
      const res = await axios.get(
        `http://localhost:5000/api/auth/findByPhone/${targetPhone}`
      );

      const u = res.data;
      setSelectedUser(u);

      if (!chatList.find((c) => c._id === u._id))
        setChatList((prev) => [u, ...prev]);

      const msgs = await axios.get(
        `http://localhost:5000/api/messages/${u._id}?currentUserId=${user.id}`
      );

      setMessages(msgs.data);
      setSearchPhone("");
    } catch (err) {
      alert("User not found!");
    }
  };

  // TYPING INDICATOR
  useEffect(() => {
    if (!socket) return;

    socket.on("typing", ({ senderId }) => {
      setTypingUser(senderId);
    });

    socket.on("stopTyping", ({ senderId }) => {
      setTypingUser(null);
    });

    return () => {
      socket.off("typing");
      socket.off("stopTyping");
    };
  }, [socket]);

  // FIXED: typing timeout should persist
  const typingTimeout = useRef(null);

  const handleTyping = () => {
    if (!socket || !selectedUser) return;

    socket.emit("typing", {
      senderId: user.id,
      receiverId: selectedUser._id,
    });

    clearTimeout(typingTimeout.current);

    typingTimeout.current = setTimeout(() => {
      socket.emit("stopTyping", {
        senderId: user.id,
        receiverId: selectedUser._id,
      });
    }, 1000);
  };

  // SEND MESSAGE
  const sendMessage = () => {
    if (!text || !selectedUser || !socket) return;

    socket.emit("sendMessage", {
      receiverPhone: selectedUser.phone,
      text,
    });

    setText("");
    setTypingUser(null);
  };

  // DELETE MESSAGE
  const deleteMessage = (messageId, receiverId) => {
    if (!socket) return;
    socket.emit("deleteMessage", { messageId, receiverId });

    setMessages((prev) => prev.filter((m) => m._id !== messageId));
  };

  // LOGOUT
  const navigate = useNavigate();
  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  return (
    <div
      style={{
        display: "flex",
        height: "90vh",
        maxWidth: 900,
        margin: "auto",
        border: "1px solid #ccc",
        borderRadius: 8,
      }}
    >
      {/* LEFT PANEL */}
      <div
        style={{
          width: 250,
          borderRight: "1px solid #ccc",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <button onClick={logout}>Logout</button>

        <div style={{ padding: 10 }}>
          <input
            type="text"
            value={searchPhone}
            onChange={(e) => setSearchPhone(e.target.value)}
            placeholder="Search by phone..."
            style={{
              width: "100%",
              padding: 6,
              borderRadius: 5,
              border: "1px solid #ccc",
            }}
          />
          <button
            onClick={() => startChat()}
            style={{ marginTop: 5, width: "100%", padding: 6 }}
          >
            Start Chat
          </button>
        </div>

        <div style={{ flexGrow: 1, overflowY: "auto" }}>
          {chatList.map((c) => (
            <div
              key={c._id}
              style={{
                padding: 10,
                cursor: "pointer",
                background: selectedUser?._id === c._id ? "#eee" : "#fff",
                display: "flex",
                alignItems: "center",
                gap: 8,
              }}
              onClick={() => startChat(c.phone)}
            >
              <div
                style={{
                  width: 10,
                  height: 10,
                  borderRadius: "50%",
                  background: c.socketId ? "green" : "gray",
                }}
              />
              <span>{c.name}</span>
            </div>
          ))}
        </div>
      </div>

      {/* RIGHT PANEL */}
      <div style={{ flexGrow: 1, display: "flex", flexDirection: "column" }}>
        {/* Messages */}
        <div
          style={{
            flexGrow: 1,
            padding: 10,
            overflowY: "auto",
            background: "#fafafa",
          }}
        >
          {messages.map((m) => (
            <div
              key={m._id}
              onMouseDown={() => handlePressStart(m._id, m.receiverId)}
              onMouseUp={handlePressEnd}
              onMouseLeave={handlePressEnd}
              onTouchStart={() => handlePressStart(m._id, m.receiverId)}
              onTouchEnd={handlePressEnd}
              onTouchCancel={handlePressEnd}
              style={{
                textAlign: m.senderId === user.id ? "right" : "left",
                marginBottom: 8,
                display: "flex",
                justifyContent:
                  m.senderId === user.id ? "flex-end" : "flex-start",
              }}
            >
              <span
                style={{
                  display: "inline-block",
                  background: m.senderId === user.id ? "#dcf8c6" : "#fff",
                  padding: 8,
                  borderRadius: 8,
                  maxWidth: "70%",
                  wordBreak: "break-word",
                }}
              >
                {m.text}
              </span>
            </div>
          ))}

          {/* TYPING INDICATOR */}
          {typingUser === selectedUser?._id && (
            <div style={{ paddingLeft: 10, color: "gray", fontSize: 12 }}>
              {selectedUser.name} is typing...
            </div>
          )}

          <div ref={messagesEndRef} />
        </div>

        {/* Input Box */}
        {selectedUser && (
          <div
            style={{
              display: "flex",
              padding: 10,
              borderTop: "1px solid #ccc",
            }}
          >
            <input
              type="text"
              value={text}
              onChange={(e) => {
                setText(e.target.value);
                handleTyping();
              }}
              placeholder="Type a message..."
              style={{
                flexGrow: 1,
                padding: 8,
                borderRadius: 5,
                border: "1px solid #ccc",
              }}
            />
            <button
              onClick={sendMessage}
              style={{
                marginLeft: 6,
                padding: "8px 12px",
                border: "none",
                borderRadius: 5,
                background: "#4CAF50",
                color: "white",
              }}
            >
              Send
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
