import { useEffect, useState, useRef } from "react";
import { io } from "socket.io-client";
import axios from "axios";


export default function ChatApp({ user, setUser }) {
  const [socket, setSocket] = useState(null);
  const [chatList, setChatList] = useState([]); // existing chats
  const [searchPhone, setSearchPhone] = useState("");
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const messagesEndRef = useRef(null);

  // Scroll chat to bottom
  const scrollToBottom = () => messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  useEffect(scrollToBottom, [messages]);

  // Socket connection
  useEffect(() => {
    const s = io("http://localhost:5000", {
      transports: ["websocket", "polling"],
      auth: { token: localStorage.getItem("token") },
    });

    s.on("connect", () => setSocket(s));

    s.on("receiveMessage", (msg) => {
      // Add message only if it's part of current chat
      if (selectedUser && (msg.senderId === selectedUser._id || msg.receiverId === selectedUser._id)) {
        setMessages(prev => [...prev, msg]);
      }
    });

    return () => s.disconnect();
  }, [selectedUser]);

  // Fetch chat history on login
  useEffect(() => {
    const fetchChats = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/chats/${user.id}`);
        setChatList(res.data);
      } catch (err) {
        console.error("Fetch chat list error:", err);
      }
    };
    fetchChats();
  }, [user.id]);

  // Start chat from search or chat list
  const startChat = async (phone) => {
    const targetPhone = (phone || searchPhone).trim();
    if (!targetPhone) return alert("Enter phone number");

    try {
      // 1️⃣ Fetch receiver by phone
      const res = await axios.get(`http://localhost:5000/api/auth/findByPhone/${targetPhone}`);
      const u = res.data;
      setSelectedUser(u);

      // 2️⃣ Add to chat list if not exists
      if (!chatList.find(c => c._id === u._id)) setChatList(prev => [u, ...prev]);

      // 3️⃣ Load previous messages
      const msgs = await axios.get(`http://localhost:5000/api/messages/${u._id}?currentUserId=${user.id}`);
      setMessages(msgs.data);

      setSearchPhone("");
    } catch (err) {
      alert("User not found!");
    }
  };

  // Send message
  const sendMessage = () => {
    if (!text || !selectedUser || !socket) return;

    // Emit to server only
    socket.emit("sendMessage", { receiverPhone: selectedUser.phone, text });

    // Clear input
    setText("");
  };
 

 
  return (
    <div className="main-container">

      <div className="left-container">
    <div style={{ display: "flex", height: "90vh", maxWidth: 900, margin: "auto", border: "1px solid #ccc", borderRadius: 8 }}>

      {/* Left panel: search + chat list */}
      <div style={{ width: 250, borderRight: "1px solid #ccc", display: "flex", flexDirection: "column" }}>
        {/* Search bar */}
        

        <div style={{ padding: 10 }}>
          <input
            type="text"
            value={searchPhone}
            onChange={(e) => setSearchPhone(e.target.value)}
            placeholder="Search by phone..."
            style={{ width: "100%", padding: 6, borderRadius: 5, border: "1px solid #ccc" }}
          />
          <button onClick={() => startChat()} style={{ marginTop: 5, width: "100%", padding: 6 }}>Start Chat</button>
        </div>
        </div>

        {/* Chat history */}
        <div style={{ flexGrow: 1, overflowY: "auto" }}>
          {chatList.map(c => (
            <div
              key={c._id}
              style={{
                padding: 10,
                cursor: "pointer",
                background: selectedUser?._id === c._id ? "#eee" : "#fff",
                display: "flex",
                alignItems: "center",
                gap: 8
              }}
              onClick={() => startChat(c.phone)}
            >
              <div style={{ width: 10, height: 10, borderRadius: "50%", background: c.socketId ? "green" : "gray" }} />
              <span>{c.name}</span>
            </div>
          ))}
        </div>
      </div>

      {/* Right panel: chat window */}
      <div className="right-container">
      <div style={{ flexGrow: 1, display: "flex", flexDirection: "column" }}>
        <div style={{ flexGrow: 1, padding: 10, overflowY: "auto", background: "#fafafa" }}>
          {messages.map((m, i) => (
            <div key={i} style={{ textAlign: m.senderId === user.id ? "right" : "left", marginBottom: 8 }}>
              <span style={{
                display: "inline-block",
                background: m.senderId === user.id ? "#dcf8c6" : "#fff",
                padding: 8,
                borderRadius: 8,
                maxWidth: "70%",
                wordBreak: "break-word"
              }}>
                {m.text}
              </span>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

        {/* Input */}
        {selectedUser && (
          <div style={{ display: "flex", padding: 10, borderTop: "1px solid #ccc" }}>
            <input
              type="text"
              value={text}
              onChange={(e) => setText(e.target.value)}
              placeholder="Type a message..."
              style={{ flexGrow: 1, padding: 8, borderRadius: 5, border: "1px solid #ccc" }}
            />
            <button onClick={sendMessage} style={{ marginLeft: 6, padding: "8px 12px", border: "none", borderRadius: 5, background: "#4CAF50", color: "white" }}>
              Send
            </button>
            
          </div>
        )}
        </div>
      </div>
    </div>
    </div>
  );
}
