import { useEffect, useState } from "react";
import { io } from "socket.io-client";
import OnlineStatus from "../components/OnlineStatus";

export default function Chat({ user }) {
  const [socket, setSocket] = useState(null);
  const [text, setText] = useState("");
  const [receiverPhone, setReceiverPhone] = useState("");
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    if (!user?.id) return;

    // âœ… Authenticated socket connection using JWT
    const newSocket = io("http://localhost:5000", {
      transports: ["websocket"],
      auth: {
        token: localStorage.getItem("token"), // ðŸ” Send token securely
      },
    });

    newSocket.on("connect", () => {
      console.log("âœ… Connected to socket.io:", newSocket.id);
      setSocket(newSocket);
    });

    newSocket.on("receiveMessage", (msg) => {
      console.log("ðŸ“© Message received:", msg);
      setMessages((prev) => [...prev, msg]);
    });

    newSocket.on("disconnect", () => {
      console.log("âŒ Socket disconnected");
      setSocket(null);
    });

    return () => newSocket.disconnect();
  }, [user]);

  const sendMessage = () => {
    if (!socket) {
      console.warn("âš ï¸ Socket not connected yet!");
      return;
    }

    if (!text || !receiverPhone) {
      alert("Please enter both phone number and message.");
      return;
    }

    socket.emit("sendMessage", {
      senderId: user.id,
      receiverPhone,
      text,
    });

    setMessages((prev) => [...prev, { senderId: user.id, text }]);
    setText("");
  };

  return (
    <div
      style={{
        maxWidth: "400px",
        margin: "auto",
        padding: "20px",
        fontFamily: "sans-serif",
        border: "1px solid #ddd",
        borderRadius: "10px",
      }}
    >
      <OnlineStatus/>
      <h2 style={{ textAlign: "center" }}>Chat App</h2>

      <div style={{ marginBottom: "10px" }}>
        <label>Receiver Phone:</label>
        <input
          type="text"
          value={receiverPhone}
          onChange={(e) => setReceiverPhone(e.target.value)}
          placeholder="Enter phone number"
          style={{
            width: "100%",
            padding: "8px",
            borderRadius: "5px",
            border: "1px solid #ccc",
            marginTop: "4px",
          }}
        />
      </div>

      <div
        style={{
          border: "1px solid #ccc",
          borderRadius: "5px",
          height: "200px",
          overflowY: "auto",
          marginBottom: "10px",
          padding: "10px",
          background: "#fafafa",
        }}
      >
        {messages.length === 0 && (
          <p style={{ textAlign: "center", color: "#999" }}>No messages yet</p>
        )}

        {messages.map((m, i) => (
          <div
            key={i}
            style={{
              textAlign: m.senderId === user.id ? "right" : "left",
              marginBottom: "8px",
            }}
          >
            <span
              style={{
                display: "inline-block",
                background: m.senderId === user.id ? "#dcf8c6" : "#fff",
                padding: "8px",
                borderRadius: "8px",
                maxWidth: "80%",
              }}
            >
              {m.text}
            </span>
          </div>
        ))}
      </div>

      <div style={{ display: "flex", gap: "6px" }}>
        <input
          type="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Type a message..."
          style={{
            flexGrow: 1,
            padding: "8px",
            borderRadius: "5px",
            border: "1px solid #ccc",
          }}
        />
        <button
          onClick={sendMessage}
          disabled={!socket}
          style={{
            padding: "8px 12px",
            border: "none",
            borderRadius: "5px",
            background: socket ? "#4CAF50" : "#999",
            color: "white",
            cursor: socket ? "pointer" : "not-allowed",
          }}
        >
          {socket ? "Send" : "Connecting..."}
        </button>
      </div>
    </div>
  );
}
