import { useEffect, useState, useRef } from "react";
import { io } from "socket.io-client";
import OnlineStatus from "../components/OnlineStatus";
import axios from "axios";

export default function Chat({ user, receiverId }) {
  const [socket, setSocket] = useState(null);
  const [text, setText] = useState("");
  const [messages, setMessages] = useState([]);
  const messagesEndRef = useRef(null);

  // Scroll to bottom
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };
  useEffect(scrollToBottom, [messages]);

  // Fetch chat history
  useEffect(() => {
    if (!user?.id || !receiverId) return;

    axios.get(`http://localhost:5000/api/messages/${receiverId}?senderId=${user.id}`)
      .then(res => setMessages(res.data))
      .catch(err => console.error("Fetch messages error:", err));
  }, [user, receiverId]);

  // Socket.io connection
  useEffect(() => {
    if (!user?.id) return;

    const newSocket = io("http://localhost:5000", {
      transports: ["websocket"],
      auth: { token: localStorage.getItem("token") },
    });

    newSocket.on("connect", () => {
      console.log("✅ Connected to socket:", newSocket.id);
      setSocket(newSocket);
    });

    newSocket.on("receiveMessage", (msg) => {
      setMessages(prev => [...prev, msg]);
    });

    newSocket.on("disconnect", () => setSocket(null));

    return () => newSocket.disconnect();
  }, [user]);

  // Send message
  const sendMessage = () => {
    if (!socket || !text) return;

    socket.emit("sendMessage", { receiverPhone: receiverId, text });
    setText("");
  };

  return (
    <div style={{ maxWidth: 400, margin: "auto", padding: 20, fontFamily: "sans-serif" }}>
      <OnlineStatus user={user} />

      <div style={{ border: "1px solid #ccc", height: 300, overflowY: "auto", padding: 10, marginBottom: 10 }}>
        {messages.map(m => (
          <div key={m._id} style={{ textAlign: m.senderId === user.id ? "right" : "left", marginBottom: 6 }}>
            {m.senderId !== user.id && <div style={{ fontSize: 12, color: "#555" }}>{m.senderId}</div>}
            <span style={{ display: "inline-block", background: m.senderId === user.id ? "#dcf8c6" : "#fff", padding: 8, borderRadius: 8, maxWidth: "80%", wordBreak: "break-word" }}>
              {m.text}
            </span>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div style={{ display: "flex", gap: 6 }}>
        <input
          type="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Type a message..."
          style={{ flexGrow: 1, padding: 8, borderRadius: 5, border: "1px solid #ccc" }}
        />
        <button onClick={sendMessage} disabled={!socket} style={{ padding: "8px 12px", borderRadius: 5, background: socket ? "#4CAF50" : "#999", color: "white" }}>
          Send
        </button>
      </div>
    </div>
  );
}
