// ChatApp.jsx
import { useEffect, useState, useRef } from "react";
import { io } from "socket.io-client";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Chat.css";

export default function ChatApp({ user, setUser }) {
  const [socket, setSocket] = useState(null);
  const [chatList, setChatList] = useState([]);
  const [searchPhone, setSearchPhone] = useState("");
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const messagesEndRef = useRef(null);
  const [typingUser, setTypingUser] = useState(null);
  const leftPanelRef = useRef(null);
  const rightPanelRef = useRef(null);


  const goToLeftPanel = () => {
  leftPanelRef.current?.classList.remove("hide");
  rightPanelRef.current?.classList.remove("show");
};

  const pressTimer = useRef(null);
  //mobile view
  useEffect(() => {
  const isMobile = window.innerWidth <= 768;
  if (isMobile) {
    setShowLeftPanel(true);     // show chat list
    setShowRightPanel(false);   // hide chat window
  } else {
    setShowLeftPanel(true);     // desktop always shows left
    setShowRightPanel(true);    // and right
  }
}, []);


  const handlePressStart = (messageId, receiverId) => {
    pressTimer.current = setTimeout(() => {
      if (window.confirm("Delete this message?")) {
        deleteMessage(messageId, receiverId);
      }
    }, 600);
  };

  const handlePressEnd = () => clearTimeout(pressTimer.current);

  const scrollToBottom = () =>
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  useEffect(scrollToBottom, [messages]);

  useEffect(() => {
    const s = io("http://localhost:5000", {
      transports: ["websocket", "polling"],
      auth: { token: localStorage.getItem("token") },
    });

    s.on("connect", () => setSocket(s));

    s.on("receiveMessage", (msg) => {
      if (
        selectedUser &&
        (msg.senderId === selectedUser._id ||
          msg.receiverId === selectedUser._id)
      ) {
        setMessages((prev) => [...prev, msg]);
      }
    });

    s.on("messageDeleted", (messageId) => {
      setMessages((prev) => prev.filter((m) => m._id !== messageId));
    });

    s.on("messageSeen", ({ messageIds }) => {
      setMessages((prev) =>
        prev.map((m) =>
          messageIds.includes(m._id) ? { ...m, seen: true } : m
        )
      );
    });

    s.on("messageDelivered", ({ messageId }) => {
      setMessages((prev) =>
        prev.map((m) =>
          m._id === messageId ? { ...m, delivered: true } : m
        )
      );
    });

    return () => s.disconnect();
  }, [selectedUser]);

  useEffect(() => {
    const fetchChats = async () => {
      try {
        const res = await axios.get(
          `http://localhost:5000/api/chats/${user.id}`
        );
        setChatList(res.data);
      } catch (err) {
        console.error("Fetch chat list error:", err);
      }
    };
    fetchChats();
  }, [user.id]);

  const startChat = async (phone) => {
    const targetPhone = (phone || searchPhone).trim();
    if (!targetPhone) return alert("Enter phone number");

    try {
      const res = await axios.get(
        `http://localhost:5000/api/auth/findByPhone/${targetPhone}`
      );

      const u = res.data;
      setSelectedUser(u);

      if (!chatList.find((c) => c._id === u._id))
        setChatList((prev) => [u, ...prev]);

      const msgs = await axios.get(
        `http://localhost:5000/api/messages/${u._id}?currentUserId=${user.id}`
      );

      setMessages(msgs.data);
      setSearchPhone("");

      socket.emit("markSeen", {
        userId: user.id,
        otherUserId: u._id,
      });
    } catch {
      alert("User not found!");
    }
  };

  useEffect(() => {
    if (!socket) return;

    socket.on("typing", ({ senderId }) => {
      setTypingUser(senderId);
    });

    socket.on("stopTyping", () => {
      setTypingUser(null);
    });

    return () => {
      socket.off("typing");
      socket.off("stopTyping");
    };
  }, [socket]);

  const typingTimeout = useRef(null);

  const handleTyping = () => {
    if (!socket || !selectedUser) return;

    socket.emit("typing", {
      senderId: user.id,
      receiverId: selectedUser._id,
    });

    clearTimeout(typingTimeout.current);

    typingTimeout.current = setTimeout(() => {
      socket.emit("stopTyping", {
        senderId: user.id,
        receiverId: selectedUser._id,
      });
    }, 1000);
  };

  const sendMessage = () => {
    if (!text || !selectedUser || !socket) return;

    socket.emit("sendMessage", {
      receiverPhone: selectedUser.phone,
      text,
      senderId: user.id,
    });

    setText("");
    setTypingUser(null);
  };

  const deleteMessage = (messageId, receiverId) => {
    if (!socket) return;

    socket.emit("deleteMessage", { messageId, receiverId });

    setMessages((prev) => prev.filter((m) => m._id !== messageId));
  };

  const navigate = useNavigate();
  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  return (
    <div className="chat-container">
      {/* LEFT PANEL */}
      <div className="left-panel" ref={leftPanelRef}>
        

        <div className="search-box">
          <input
            type="text"
            value={searchPhone}
            onChange={(e) => setSearchPhone(e.target.value)}
            placeholder="Search by phone..."
            className="search-input"
          />
          <button className="start-chat-btn" onClick={() => startChat()}>
            Start Chat
          </button>
        </div>

        <div className="chat-list">
          {chatList.map((c) => (
            <div
              key={c._id}
              className={`chat-item ${
                selectedUser?._id === c._id ? "active" : ""
              }`}
              onClick={() => startChat(c.phone)}
            >
              <span
                className={`status-dot ${c.socketId ? "online" : "offline"}`}
              ></span>
              <span>{c.name}</span>
            </div>
          ))}
        </div>
      </div>

      {/* RIGHT PANEL */}
      <div className="right-panel"ref={rightPanelRef}>
        <div className="messages-area">
          {messages.map((m) => (
            <div
              key={m._id}
              className={`message-row ${
                m.senderId === user.id ? "sent" : "received"
              }`}
              onMouseDown={() => handlePressStart(m._id, m.receiverId)}
              onMouseUp={handlePressEnd}
              onMouseLeave={handlePressEnd}
              onTouchStart={() => handlePressStart(m._id, m.receiverId)}
              onTouchEnd={handlePressEnd}
              onTouchCancel={handlePressEnd}
            >
              <span
                className={`message-bubble ${
                  m.senderId === user.id ? "sent-bubble" : "received-bubble"
                }`}
              >
                {m.text}
                {m.senderId === user.id && (
                  <div className="tick">
                    {!m.delivered && !m.seen && <span>✔</span>}
                    {m.delivered && !m.seen && <span>✔✔</span>}
                    {m.seen && <span className="seen">✔✔</span>}
                  </div>
                )}
              </span>
            </div>
          ))}

          {typingUser === selectedUser?._id && (
            <div className="typing-indicator">
              {selectedUser.name} is typing...
            </div>
          )}

          <div ref={messagesEndRef} />
        </div>

        {selectedUser && (
          <div className="input-area">
            <input
              type="text"
              value={text}
              onChange={(e) => {
                setText(e.target.value);
                handleTyping();
              }}
              placeholder="Type a message..."
              className="text-input"
            />
            <button className="send-btn" onClick={sendMessage}>
              Send
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
