import { useEffect, useState, useRef } from "react";
import { io } from "socket.io-client";
import axios from "axios";
import "./Chat.css";

export default function ChatApp({ user, setUser }) {
  const [socket, setSocket] = useState(null);
  const [chatList, setChatList] = useState([]);
  const [searchPhone, setSearchPhone] = useState("");
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");

  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Socket setup
  useEffect(() => {
    const s = io("http://localhost:5000", {
      transports: ["websocket", "polling"],
      auth: { token: localStorage.getItem("token") },
    });

    setSocket(s);

    s.on("receiveMessage", (msg) => {
      if (selectedUser && (msg.senderId === selectedUser._id || msg.receiverId === selectedUser._id)) {
        setMessages((prev) => [...prev, msg]);
      }
    });

    return () => s.disconnect();
  }, [selectedUser]);

  // Fetch chat list
  useEffect(() => {
    const fetchChats = async () => {
      const res = await axios.get(`http://localhost:5000/api/chats/${user.id}`);
      setChatList(res.data);
    };
    fetchChats();
  }, [user.id]);

  // Start chat
  const startChat = async (phone) => {
    const targetPhone = phone || searchPhone.trim();
    if (!targetPhone) return alert("Enter phone number");

    try {
      const res = await axios.get(`http://localhost:5000/api/auth/findByPhone/${targetPhone}`);
      const u = res.data;
      setSelectedUser(u);

      if (!chatList.find((c) => c._id === u._id)) {
        setChatList((prev) => [u, ...prev]);
      }

      const msgs = await axios.get(
        `http://localhost:5000/api/messages/${u._id}?currentUserId=${user.id}`
      );
      setMessages(msgs.data);
      setSearchPhone("");
    } catch {
      alert("User not found!");
    }
  };

  // Send message
  const sendMessage = () => {
    if (!text || !socket || !selectedUser) return;

    socket.emit("sendMessage", {
      receiverPhone: selectedUser.phone,
      text,
    });

    setText("");
  };

  return (
    <div className="main-container">
      <div className="chat-wrapper">

        {/* LEFT PANEL */}
        <div className="chat-left">
          <div className="chat-search">
            <input
              value={searchPhone}
              onChange={(e) => setSearchPhone(e.target.value)}
              placeholder="Search by phone..."
            />
            <button onClick={() => startChat()}>Start Chat</button>
          </div>

          <div className="chat-list">
            {chatList.map((c) => (
              <div
                key={c._id}
                className={`chat-list-item ${selectedUser?._id === c._id ? "active" : ""}`}
                onClick={() => startChat(c.phone)}
              >
                <div className={`chat-online-dot ${c.socketId ? "online" : ""}`} />
                <span>{c.name}</span>
              </div>
            ))}
          </div>
        </div>

        {/* RIGHT PANEL */}
        <div className="chat-right">
          <div className="chat-messages">
            {messages.map((m, i) => (
              <div key={i} className="message-wrapper">
                <div
                  className={`message-bubble ${
                    m.senderId === user.id ? "message-mine" : "message-their"
                  }`}
                >
                  {m.text}
                </div>
              </div>
            ))}
            <div ref={messagesEndRef}></div>
          </div>

          {selectedUser && (
            <div className="chat-input-bar">
              <input
                value={text}
                onChange={(e) => setText(e.target.value)}
                placeholder="Type a message..."
              />
              <button onClick={sendMessage}>Send</button>
            </div>
          )}
        </div>

      </div>
    </div>
  );
}
