import { useState, useEffect, useRef } from "react";
import axios from "axios";
import { motion } from "framer-motion";

const personas = [
  { id: "friendly", name: "Friendly AI", description: "Polite & helpful" },
  { id: "sarcastic", name: "Sarcastic AI", description: "Funny & witty" },
  { id: "coder", name: "Coder AI", description: "Expert developer" },
  { id: "romantic", name: "Romantic AI", description: "Loving & sweet" },
];

export default function AIChat() {
  const [selectedPersona, setSelectedPersona] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const sendMessage = async () => {
    if (!input.trim() || !selectedPersona) return;

    // Add user message locally
    const userMsg = { sender: "user", text: input };
    setMessages((prev) => [...prev, userMsg]);
    setInput("");
    setLoading(true);

    try {
      // Convert messages to backend format
      const formattedMessages = messages.map((msg) => ({
        role: msg.sender === "user" ? "user" : "assistant",
        content: msg.text,
      }));
      formattedMessages.push({ role: "user", content: input });

      const res = await axios.post("http://localhost:5000/api/ai/chat", {
        messages: formattedMessages,
        persona: selectedPersona,
      });

      const aiReply = res.data.assistantMessage.content;

      setMessages((prev) => [...prev, { sender: "ai", text: aiReply }]);
    } catch (error) {
      setMessages((prev) => [...prev, { sender: "ai", text: "Error contacting AI" }]);
    }

    setLoading(false);
  };

  return (
    <div className="flex h-screen bg-[#eae6df]">
      {/* LEFT PANEL — PERSONA LIST */}
      <div className="w-[30%] bg-[#ffffff] border-r border-gray-300 p-4 overflow-y-auto">
        <h2 className="text-xl font-bold mb-4">AI Personas</h2>
        {personas.map((p) => (
          <motion.div
            whileHover={{ scale: 1.02 }}
            key={p.id}
            onClick={() => setSelectedPersona(p.id)}
            className={`p-4 mb-2 rounded-lg cursor-pointer transition-all hover:bg-[#f0f2f5] border-b
            ${selectedPersona === p.id ? "bg-blue-600 text-white" : "bg-gray-200"}`}
          >
            <h3 className="font-semibold">{p.name}</h3>
            <p className="text-sm">{p.description}</p>
          </motion.div>
        ))}
      </div>

      {/* RIGHT PANEL — CHAT WINDOW */}
      <div className="flex-1 flex flex-col bg-[#efeae2]">
        <div className="flex-1 p-4 overflow-y-auto">
          {selectedPersona ? (
            messages.map((msg, index) => (
              <div
                key={index}
                className={`flex mb-3 ${msg.sender === "user" ? "justify-end" : "justify-start"}`}
              >
                <div
                  className={`p-3 rounded-2xl max-w-xs shadow
                  ${msg.sender === "user" ? "bg-blue-600 text-white" : "bg-white"}`}
                >
                  {msg.text}
                </div>
              </div>
            ))
          ) : (
            <div className="text-center text-gray-500 mt-20 text-lg">
              Select a persona to start chatting
            </div>
          )}
          <div ref={messagesEndRef}></div>
        </div>

        {/* INPUT BAR */}
        {selectedPersona && (
          <div className="p-4 flex gap-3 bg-white shadow-lg">
            <input
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type a message..."
              className="flex-1 p-3 border rounded-xl"
            />
            <button
              onClick={sendMessage}
              className="px-4 py-2 bg-blue-600 text-white rounded-xl disabled:bg-blue-300"
              disabled={loading}
            >
              Send
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
